import React from "react";
import styled from "styled-components";
import { Button } from "./Button";

const StyledTable = styled.div`
  background: #bcff78;
  height: auto;
  h1 {
    margin-left: 5vw;
    margin-right: 5vw;
    padding: 10px;
    text-align: center;
    background: #fff;
    color: #ff96ad;
    border-radius: 40px;
  }
  .fecha {
    margin: 5vw 0 0 5vw;
    padding-top: 3vh;
  }
  .input-fecha {
    text-decoration: none;
    height: 30px;
    border: none;
    border-radius: 20px;
  }
  .btn-editar {
    position: relative;
    bottom: 10px;
  }
  .teacher-info {
    background: #ff96ad;
  }
`;
export function Table() {
  return (
    <StyledTable>
      <div className="fecha">
        <label className="label-fecha" for="fecha">
          Seleccionar por fecha:{" "}
        </label>
        <input className="input-fecha" type="date" name="fecha" />
      </div>
      <h1>Descripci√≥n</h1>

      <div className="teacher-info">
        <Button
          className="btn-editar"
          type="button"
          value="Editar"
          backgroundColor="#FF7878"
        />
        <Button type="button" value="Eliminar" backgroundColor="#4E4E4E" />
      </div>
    </StyledTable>
  );
}
